import {connect} from "react-redux";
import {useNavigate} from "react-router-dom";
import {useState} from "react";

import './style.css';
import '../../styles.css';

import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';

const NewPoll = ({dispatch}) => {
    const navigate = useNavigate();
    const [firstOption, setFirstOption] = useState("");
    const [secondOption, setSecondOption] = useState("");

    const handleFirstOptionChange = (e) => {
        const value = e.target.value;
        setFirstOption(value);
    };

    const handleSecondOptionChange = (e) => {
        const value = e.target.value;
        setSecondOption(value);
    };

    const handleSubmit = (e) => {
        e.preventDefault();

        navigate("/");
    };

    return (
        <div>
            <h1>New Poll</h1>

            <Form onSubmit={handleSubmit}>
                <Form.Group className="mb-3" controlId="firstOption">
                    <Form.Label>First Option</Form.Label>
                    <Form.Control type="text" placeholder="Enter the first option" value={firstOption}
                    onChange={handleFirstOptionChange}/>
                </Form.Group>

                <Form.Group className="mb-3" controlId="secondOption">
                    <Form.Label>Second Option</Form.Label>
                    <Form.Control type="text" placeholder="Enter the second option" value={secondOption}
                    onChange={handleSecondOptionChange} />
                </Form.Group>
                <Button variant="primary" type="submit">
                    Submit
                </Button>
            </Form>
        </div>
    );
}

export default connect()(NewPoll);
