import {connect} from "react-redux";
import {useNavigate} from "react-router-dom";
import {useState} from "react";

import './style.css';
import '../../styles.css';

import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';

const NewPoll = ({dispatch}) => {
    const navigate = useNavigate();
    const [firstOption, setFirstOption] = useState("");
    const [secondOption, setSecondOption] = useState("");

    const handleFirstOptionChange = (e) => {
        const value = e.target.value;
        setFirstOption(value);
    };

    const handleSecondOptionChange = (e) => {
        const value = e.target.value;
        setSecondOption(value);
    };

    const handleSubmit = (e) => {
        e.preventDefault();

        navigate("/");
    };

    return (
        <div>
            <h1>New Poll</h1>
            <form onSubmit={handleSubmit}>
                <label htmlFor="firstOption">First Option</label>
                <input
                    value={firstOption}
                    onChange={handleFirstOptionChange}
                    type="text"
                    id="firstOption"/>
                <br/>
                <label htmlFor="secondOption">Second Option</label>
                <input
                    value={secondOption}
                    onChange={handleSecondOptionChange}
                    type="text"
                    id="secondOption"/>
                <br/>
                <button type="submit">Submit</button>
            </form>

            <Form>
                <Form.Group className="mb-3" controlId="formBasicEmail">
                    <Form.Label>First Option</Form.Label>
                    <Form.Control type="text" placeholder="Enter the first option" />
                </Form.Group>

                <Form.Group className="mb-3" controlId="formBasicPassword">
                    <Form.Label>Second Option</Form.Label>
                    <Form.Control type="text" placeholder="Enter the second option" />
                </Form.Group>
                <Button variant="primary" type="submit">
                    Submit
                </Button>
            </Form>
        </div>
    );
}

export default connect()(NewPoll);
